import { useEffect, useState } from "react";
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios'
function Dashborad() {

    const url = "http://127.0.0.1:5000/product";
    const [products, setProducts] = useState([]);
    const [product, setProduct] = useState({ productid: "", producttitle: "", price: "", stock: "" })


    const FetchRecord = () => {
        axios.get(url).then((result) => {

            setProducts(result.data);

        }, []);

    }


   const RemoveRecord=(id)=>{
    var delurl = url+"/"+id;
    axios.delete(delurl).then((result)=>{
        if(result.data.afftedRows != undefined && result.data.afftedRows > 0 )
        {
            FetchRecord();
        }
    })
   }
    const UpdateRecord = () => {

    }

    useEffect(() => {
        FetchRecord();
    })

    return (
        <div className="container">
            <br /><br /><br /><br />
            <div className="table-responsive" >

                <table className="table table-bordered">
                    <tbody>
                        <tr>
                            <td>No : </td>
                            <td><input type="number" name="productid" value={product.productid}></input></td>
                        </tr>
                        <tr>
                            <td>Tittle : </td>
                            <td><input type="text" name="producttitle" value={product.producttitle}></input></td>
                        </tr>
                        <tr>
                            <td>Price : </td>
                            <td><input type="number" name="p" value={product.price}></input></td>
                        </tr>
                        <tr>
                            <td>Stock : </td>
                            <td><input type="number" name="p" value={product.stock}></input></td>
                        </tr>
                        <tr>
                            <td colspan="2">
                                <button className='btn btn-primary' onClick={AddRecord} >Add Record</button>
                                <button className='btn btn-success' onClick={UpdateRecord}>Update Record</button>
                                <button className='btn btn-info' onClick={ResetRecord}>Reset Record</button>
                            </td>
                        </tr>
                    </tbody>

                </table>

            </div>
            <hr /><hr />

            <div className="table-responsive">
                <table className="table table-borderd">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Tittle</th>
                            <th>Price</th>
                            <th>Stock</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            products.map((product) => {
                                return (<tr>

                                    <td>{product.productid}</td>
                                    <td>{product.producttitle}</td>
                                    <td>{product.price}</td>
                                    <td>{product.stock}</td>
                                    <td> <button className="btn btn-warning">EDIT</button></td>
                                    <td> <button className="btn btn-danger" onClick={() => {
                                        RemoveRecord(product.productid);
                                    }}>DELETE</button></td>
                                </tr>)
                            })
                        }
                    </tbody>

                </table>
            </div>

        </div>



    );


}

export default Dashborad;
